import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Map, GoogleApiWrapper } from 'google-maps-react';

const Main = ({ carModel, hazardRating, path, google }) => {

    const [pos, setCoordinates] = useState ({
        lat: null,
        long: null
    })

    const { lat, long } = pos;

    const onChange = e => setCoordinates({ ...pos, [e.target.name]: e.target.value });

    const mapStyles = {
        width: '100%',
        height: '100%'
    };

    return(
        <div className='grid-ver'>
            <div className='grid-hor' style={{ paddingBottom : '2rem' }}>
                <div className='grid-ver'>
                    <div>
                        <h1 className='text-primary' style={{ fontSize: '3rem', font: 'Candal', fontWeight: 'bolder' }}>Car Model</h1>
                        <body style={{ fontSize: '2rem' }}>{carModel}</body>
                    </div>
                    <div>
                        <label
                            htmlFor='name'
                            className='login-label'
                            style={{ textAlign: 'right' }}
                        >
                            Latitude
                        </label>
                        <input
                            type='text'
                            name='lat'
                            value={lat}
                            placeholder='e.g., 47.444'
                            onChange={onChange}
                            required
                            style={{ marginLeft: 'auto', textAlign: 'right' }}
                        />
                        <label
                            htmlFor='email'
                            className='login-label'
                            style={{ textAlign: 'right' }}
                        >
                            Longitude
                        </label>
                        <input
                            type='email'
                            name='long'
                            value={long}
                            placeholder='e.g., -122.176'
                            onChange={onChange}
                            required
                            style={{ marginLeft: 'auto', textAlign: 'right' }}
                        />
                    </div>
                </div>
                <div style={{ position: 'relative', width: '32rem', height: '25rem' }}>
                    <Map
                        google={google}
                        zoom={8}
                        style={mapStyles}
                        initialCenter={{ lat: 47.444, lng: -122.176 }}
                    />
                </div>
            </div>
            <div className='grid-hor'>
                <div style={{ left: '-20rem' }}>
                    <h1 className='text-primary' style={{ fontSize: '3rem', font: 'Candal', fontWeight: 'bolder' }}>Hazard Rating</h1>
                    <body style={{ fontSize: '2rem' }}>{hazardRating}</body>
                </div>
                <div>
                    <h1 className='text-primary' style={{ fontSize: '3rem', font: 'Candal', fontWeight: 'bolder' }}>Shortest Path</h1>
                    <body>{path}</body>
                </div>
            </div>
        </div>
    );
}

Main.propTypes = {
    carModel: PropTypes.string.isRequired,
    hazardRating: PropTypes.number,
    path: PropTypes.string
};

Main.defaultProps = {
    carModel: 'Volkswagen Beetle',
    hazardRating: 0,
    path: ''
};


export default GoogleApiWrapper({
    apiKey: 'AIzaSyCdN-PaaKG8Bh5xOhQLpWmAuGNX_0e3b2g'
})(Main);
