import React, { useReducer } from 'react';
import axios from 'axios';
import {
    GET_HAZARD,
    GET_DATA,
    MAP_ERROR
  } from '../types';
import MapReducer from './mapReducer';
import MapContext from './mapContext';

const MapState = props => {

    const [state, dispatch] = useReducer(MapReducer, initialState);

    // Get the hazard rating
    const getHazard = async (curr, dest, carModel) => {
        const config = {
            headers: {
              'Content-Type': 'application/json'
            }
        };
    
        const body = {
            curr: curr,
            dest: dest,
            carModel: carModel
        };

        try {
            const res = await axios.post(`/hazard`, body);
            dispatch({ type: GET_HAZARD, payload: res.data });
        } catch (error) {
            dispatch({ type: MAP_ERROR });
        }
    };

    const getData = () => {

    }

    return (
        <MapContext.Provider 
            value={{
                getHazard,
                getData
            }}
        >
            {props.children}
        </MapContext.Provider>
    );
};

export default MapState;
